mapper.select:
SELECT

FROM country
WHERE code = #{code, jdbcType=CHAR}

mapper.update:
UPDATE country
SET code = #{code, jdbcType=CHAR}, name = #{name, jdbcType=CHAR}, continent = #{continent, jdbcType=CHAR}, region = #{region, jdbcType=CHAR}, surfacearea = #{surfaceArea, jdbcType=DECIMAL}, indepyear = #{indepYear, jdbcType=SMALLINT}, population = #{population, jdbcType=INTEGER}, lifeexpectancy = #{lifeExpectancy, jdbcType=DECIMAL}, gnp = #{gnp, jdbcType=DECIMAL}, gnpold = #{gNPOld, jdbcType=DECIMAL}, localname = #{localName, jdbcType=CHAR}, governmentform = #{governmentForm, jdbcType=CHAR}, headofstate = #{headOfState, jdbcType=CHAR}, capital = #{capital, jdbcType=INTEGER}, code2 = #{code2, jdbcType=CHAR}
WHERE code = #{code, jdbcType=CHAR}
